typedef enum {
	GYPSY_ACCURACY_FIELDS_NONE = 0,
	GYPSY_ACCURACY_FIELDS_POSITION = 1 << 0,
	GYPSY_ACCURACY_FIELDS_HORIZONTAL = 1 << 1,
	GYPSY_ACCURACY_FIELDS_VERTICAL = 1 << 2,
} GypsyAccuracyFields;
